import React from "react"
import Question from "./Question"

export default function PlayScreen(props) {

    const {playingGame, toStartScreen, toEndGame, gameQuestionsDirty} = props

    // End Game Stats keep track of total correct answers and total selected answers
    const [allQuestionStats, setAllQuestionStats] = React.useState(
        gameQuestionsDirty.map((question, index) => (
        {
            key: index,
            id: index,
            correct: false,
            answered: false
        })
    ))

    React.useEffect(()=>console.log(endGameStats.totalAnswered),[endGameStats])

    // Update total correct and total selected based on user input
    function updateEndGameStats(correctCount, answeredCount){
        setEndGameStats(prevStats => (
            {
                totalCorrect: prevStats.totalCorrect + correctCount,
                totalAnswered: prevStats.totalAnswered + answeredCount
            }
        ))
    }

    // Generate question elements to display
	const questionElements = gameQuestionsDirty.map((questionElement, index) => {
		return <Question 
			key={allQuestionStats[index].id}
			id={allQuestionStats[index].id}
			questionText={questionElement.question}
			questionAnswers={[...questionElement.incorrect_answers, questionElement.correct_answer]}
			playingGame={playingGame}
            updateEndGameStats={updateEndGameStats}
		/>
	})

    return (
        <div className="play-screen">
            <div className="question-container">
                {questionElements}
            </div>

            {playingGame && 
            <button className="submit-button" 
            onClick={endGameStats.totalAnswered === questionElements.length ? toEndGame : undefined}>
                Check Answers
            </button>}

            {!playingGame && <div className="play-again-container">
                <h2 className="score">
                    You scored {endGameStats.totalCorrect}/{questionElements.length} correct answers
                </h2>
                <button className="play-again-button" onClick={toStartScreen}>
                    Play Again
                </button>
            </div>
            }
        </div>
    )
}
