export default function PlayScreen(props) {

    const {playingGame, 
        toStartScreen, toEndGame, totalCorrect, gameQuestionsDirty} = props

    //const [allQuestions, setAllQuestions] = React.useState([])

    function generateNewQuestions(gameQuestionsDirty){
        let questions = []
        for (let i = 0; i < gameQuestionsDirty.length; i++) {
            let answers = [...gameQuestionsDirty[i].incorrect_answers, gameQuestionsDirty[i].correct_answer]
            let answersArray = answers.map(answer => (
                {
                    id: nanoid(),
                    text: answer,
                    selected: false,
                    correct: false
                }
            ))
            answersArray[answersArray.length - 1].correct = true
            for (let i = answersArray.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                let temp = answersArray[i];
                answersArray[i] = answersArray[j];
                answersArray[j] = temp;
            }
            questions[i] = 
            {
                id: i,
                answers: answersArray,
                question: gameQuestionsDirty[i].question
            }
        }
        setAllQuestions(questions)
    }

	const questionElements = gameQuestionsDirty.map((questionElement, index) => {
		return <Question 
			key={index}
			id={index}
			question={questionElement.question}
			handleAnswerClick={handleAnswerClick}
			answers={[...questionElement.incorrect_answers, questionElement.correct_answer]}
			gameState={gameState}
		/>
	})

    return (
        <div className="play-screen">
            <div className="question-container">
                {questionElements}
            </div>
            {
            playingGame && 
            <button 
                className="submit-button" 
                onClick={toEndGame}
            >Check Answers
            </button>
            }
            {
            !playingGame && 
            <div className="play-again-container">
                <h2 className="score">
                    You scored {totalCorrect}/{questionElements.length} correct answers
                </h2>
                <button 
                    className="play-again-button" 
                    onClick={toStartScreen}
                >Play Again
                </button>
            </div>
            }
        </div>
    )
}
