import React from 'react'
import {nanoid} from 'nanoid'

export default function Question(props) {

    const [answers, setAnswers] = React.useState(props.answers)

    function handleAnswerClick(event){

        setAnswers(prevAnswers => prevAnswers.answers.map(answer => (
            event.target.id === answer.id ? 
                {...answer, selected: !answer.selected} : {...answer, selected: false}
        )))

    }

    const answerElements = answers.map(answer => (
        <button className={`question-answer${answer.selected ? " selected" : ""}`}
                key={answer.id}
                id={answer.id}
                onClick={handleAnswerClick}
                dangerouslySetInnerHTML={{__html: answer.text}}
        >
        </button>
    ))


    return (
        <div className="question">
            <h2 className="question-header"
                dangerouslySetInnerHTML={{__html: props.question}}
            >
            </h2>
            <div className="question-choices">
                {answerElements}
            </div>
        </div>
    )
}
