import React from 'react'
import {nanoid} from 'nanoid'
import Question from './components/Question'
import StartScreen from './components/StartScreen'
import PlayScreen from './components/PlayScreen'

export default function App() {

	// Grab category: id data from API once
	const [categories, setCategories] = React.useState([])
	React.useEffect(() => {
		fetch("https://opentdb.com/api_category.php")
			.then(res => res.json())
			.then(data => setCategories(data.trivia_categories))
	}, [])

	// Generate form to hold user input
	const [formData, setFormData] = React.useState(
		{
			numQuestions: "",
			category: 0,
			difficulty: "",
			type: ""
		}
	)

	function handleFormChange(event) {
		const {name, value} = event.target
		setFormData(prevFormData => (
			{
				...prevFormData,
				[name]: value
			}
		))
	}

	// Game state tracks which screen to render
	const [playingGame, setPlayingGame] = React.useState(false)

	function generateUrl() {
		let {numQuestions, category, difficulty, type} = formData

		numQuestions = `amount=${numQuestions}`
		category = category === 0 ? "" : `&category=${category}`
		difficulty = difficulty === "" ? "" : `&difficulty=${difficulty}`
		type = type === "" ? "" : `&type=${type}`

		return `https://opentdb.com/api.php?${numQuestions}
			${category}${difficulty}${type}`
	}

	function startGame(){
		fetch(generateUrl())
			.then(res => res.json())
			.then(data => generateNewQuestions(data.results))

		setPlayingGame(true)

		setFormData(
			{
				numQuestions: "",
				category: 0,
				difficulty: "",
				type: ""
			}
		)
	}

	function toStartScreen(){
		setPlayingGame(false)
	}
	// React.useEffect(() => {

	// 	const url = `https://opentdb.com/api.php?
	// 					amount=${formData.numQuestions}
	// 					${"&category="}`
	// 	fetch("https://opentdb.com/api.php?amount=3")
	// 		.then(res => res.json())
	// 		.then(data => generateNewQuestions(data.results))
	// }, [])

	function generateNewQuestions(questionsArray){
		setAllQuestions(questionsArray)
	}

	const [allQuestions, setAllQuestions] = React.useState([])

	const questionElements = allQuestions.map(questionElement => {
		let answersArray = questionElement.incorrect_answers.map(answer => 
			({
				key: nanoid(),
				text: answer,
				selected: false,
				correct: false
			}))
		answersArray.push(
			{
				key: nanoid(),
				text: questionElement.correct_answer,
				selected: false,
				correct: true
			}
		) 
		return <Question 
			key={nanoid()}
			question={questionElement.question}
			answers={answersArray}
		/>
	})

	const categoryOptions = categories.map(category => (
		<option 
			key={nanoid()}
			value={category.id}
		>{category.name}</option>
	))

	return (
		<div className="main-container">
			{
			!playingGame && 
			<StartScreen 
				formData={formData}
				handleFormChange={handleFormChange}
				categoryOptions={categoryOptions}
				startGame={startGame}
			/>
			}
			{
			playingGame && 
			<PlayScreen 
				questionElements={questionElements}
				toStartScreen={toStartScreen}
			/>
			}
		</div>
	)
}
