import React from 'react'
import {nanoid} from 'nanoid'

export default function Question(props) {

    const {id, questionText, questionAnswers, gameState} = props

    const [question, setQuestion] = React.useState(generateNewQuestion())

    function generateNewQuestion(){
        let answersArray = questionAnswers.map(answer => (
            {
                id: nanoid(),
                text: answer,
                selected: false,
                correct: false
            }
        ))
        answersArray[answersArray.length - 1].correct = true
        for (let i = answersArray.length - 1; i > 0; i--) {
            let j = Math.floor(Math.random() * (i + 1));
            let temp = answersArray[i];
            answersArray[i] = answersArray[j];
            answersArray[j] = temp;
        }
        return  {
                    id: id,
                    answers: answersArray,
                    question: questionText
                }
        }

    function answerStyle(answer){
        if(gameState === "playingGame"){
            return answer.selected ? " selected" : ""
        }
        else if(gameState === "endGame"){
            if(answer.correct){
                return " correct"
            }
            else if(answer.selected){
                return " incorrect"
            }
            else {
                return " blank"
            }
        }
    }

    function handleAnswerClick(event, questionIndex){
		setAllQuestions(prevQuestions => prevQuestions.map(question => {
			if (question.id === questionIndex) {
				let updatedAnswers = question.answers.map(answer => (
					event.target.id === answer.id ? 
                	{...answer, selected: !answer.selected} : {...answer, selected: false}
				))
				return {...question, answers: updatedAnswers}
			}
			else {
				return question
			}
		}
		))
    }

    const answerElements = question.answers.map(answer => (
        <button className={`question-answer${answerStyle(answer)}`}
                key={answer.id}
                id={answer.id}
                onClick={gameState === "playingGame" ? 
                (event) => handleAnswerClick(event) : undefined}
                dangerouslySetInnerHTML={{__html: answer.text}}
        >
        </button>
    ))

    return (
        <div className="question">
            <h2 className="question-header"
                dangerouslySetInnerHTML={{__html: props.question}}
            >
            </h2>
            <div className="question-choices">
                {answerElements}
            </div>
        </div>
    )
}
