export default function Question(props) {

    const answerArray = props.answers.map(answer => (
        <button>{answer}</button>
    ))

    return (
        <div className="question">
            <h2 className="question-header">{props.question}</h2>
            <div className="question-choices">
                {answerArray}
            </div>
        </div>
    )
}
