export default function PlayScreen(props) {

    const {playingGame, questionElements, 
        toStartScreen, toEndGame, totalCorrect} = props

    function generateNewQuestions(questionsArray){
        let questions = []
        for (let i = 0; i < questionsArray.length; i++) {
            let answers = [...questionsArray[i].incorrect_answers, questionsArray[i].correct_answer]
            let answersArray = answers.map(answer => (
                {
                    id: nanoid(),
                    text: answer,
                    selected: false,
                    correct: false
                }
            ))
            answersArray[answersArray.length - 1].correct = true
            for (let i = answersArray.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                let temp = answersArray[i];
                answersArray[i] = answersArray[j];
                answersArray[j] = temp;
            }
            questions[i] = 
            {
                id: i,
                answers: answersArray,
                question: questionsArray[i].question
            }
        }
        setAllQuestions(questions)
    }

    return (
        <div className="play-screen">
            <div className="question-container">
                {questionElements}
            </div>
            {
            playingGame && 
            <button 
                className="submit-button" 
                onClick={toEndGame}
            >Check Answers
            </button>
            }
            {
            !playingGame && 
            <div className="play-again-container">
                <h2 className="score">
                    You scored {totalCorrect}/{questionElements.length} correct answers
                </h2>
                <button 
                    className="play-again-button" 
                    onClick={toStartScreen}
                >Play Again
                </button>
            </div>
            }
        </div>
    )
}
